INSERT INTO message (id, channelId, userId, message, createdAt, updatedAt) VALUES ('1226339814475829319', '1178822630225227818', '139462049976549377', 'so I found out our database stores it''s messages in millisecond epoch time, but SQLite only has access to second epoch time.\n\nMessed with teh prompt and it''s doing a good job with the rag:\n\nUser\nYou\nHello\n\nAssistant\nAssistant\nHi there! How can I assist you today?\n\nUser\nYou\nHas anybody been active today?\n\nAssistant\nAssistant\nYes, Ben Werner has been active today, sending messages at various times. If you need more detailed information or have any specific queries, feel free to ask!\n\nUser\nYou\nWhat did he talk about?\n\nAssistant\nAssistant\nToday, Ben Werner talked about the following topics in his messages:\n\nHe asked, ""what is your name?""\nHe also sent a greeting message, saying ""hi there""\nIf you need further details or have more specific questions, feel free to let me know!\n\nUser\nYou\nHas anyone else been active today?\n\nAssistant\nAssistant\nIt appears that Ben Werner is the only user who has been active today. If there''s anything else you need or further information I can provide, just let me know!', 1712452596063, 1712452596063);
INSERT INTO message (id, channelId, userId, message, createdAt, updatedAt) VALUES ('1226343696828600440', '1178822630225227818', '139462049976549377', 'I got it to work really really well.\n\nFOr example I asked what were people talking aboput yesterday and it generated this:\n\n```sql\nSELECT message.message, message.createdAt, user.fullName, channel.slug AS channelName \nFROM message \nJOIN user ON message.userId = user.userId \nJOIN channel ON message.channelId = channel.id \nWHERE message.createdAt >= (strftime(''%s'', ''now'', ''-2 days'') + 0) * 1000\nAND message.createdAt < (strftime(''%s'', ''now'', ''-1 day'') + 0) * 1000\nLIMIT 100\n```', 1712453521688, 1712453521688);
